(()=>{"use strict";function appendValue(e){document.getElementById("result").value+=e}function clearResult(){document.getElementById("result").value=""}function calculateResult(){const result=document.getElementById("result");try{result.value=eval(result.value)}catch{result.value="Error"}}document.querySelectorAll(".btn").forEach((e=>{e.addEventListener("click",(e=>{const t=e.target.value;"C"===t?clearResult():"="===t?calculateResult():appendValue(t)}))}))})();